import{_ as e,c as t,o as l,aK as _}from"./chunks/framework.gpVTZwhR.js";const c=JSON.parse('{"title":"奇函数和偶函数加减乘除的规律","description":"","frontmatter":{},"headers":[{"level":2,"title":"1、奇偶函数的加法规则","slug":"_1、奇偶函数的加法规则","link":"#_1、奇偶函数的加法规则","children":[]},{"level":2,"title":"2、奇偶函数的减法规则","slug":"_2、奇偶函数的减法规则","link":"#_2、奇偶函数的减法规则","children":[]},{"level":2,"title":"3、奇偶函数的乘法规则","slug":"_3、奇偶函数的乘法规则","link":"#_3、奇偶函数的乘法规则","children":[]},{"level":2,"title":"4、奇偶函数的除法规则","slug":"_4、奇偶函数的除法规则","link":"#_4、奇偶函数的除法规则","children":[]}],"relativePath":"gaozhong/数学/自己总结/奇函数和偶函数加减乘除的规律.md","filePath":"gaozhong/数学/自己总结/奇函数和偶函数加减乘除的规律.md","lastUpdated":1739404680000}'),p={name:"gaozhong/数学/自己总结/奇函数和偶函数加减乘除的规律.md"};function o(r,a,i,n,s,d){return l(),t("div",null,a[0]||(a[0]=[_('<h1 id="奇函数和偶函数加减乘除的规律" tabindex="-1">奇函数和偶函数加减乘除的规律 <a class="header-anchor" href="#奇函数和偶函数加减乘除的规律" aria-label="Permalink to &quot;奇函数和偶函数加减乘除的规律&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>把奇当负数，把偶当正数，正数加负数不一定为正数也不一定为负数，就非奇非偶嘛。</p></div><p>对于一个定义域关于原点对称的函数f（x）的定义域内任意一个x，都有f（-x）=-f（x），那么函数f（x）就叫做奇函数。</p><p>如果对于函数f(x)的定义域内任意的一个x，都有f(x)=f(-x),那么函数f(x)就叫做偶函数。</p><p>奇偶函数的加减乘除：奇函数±奇函数=奇函数；奇函数±偶函数=非奇非偶函数。</p><p>奇函数和偶函数加减乘除的规律：</p><h2 id="_1、奇偶函数的加法规则" tabindex="-1">1、奇偶函数的加法规则 <a class="header-anchor" href="#_1、奇偶函数的加法规则" aria-label="Permalink to &quot;1、奇偶函数的加法规则&quot;">​</a></h2><p>（1）奇函数加奇函数所得函数为奇函数。</p><p>（2）偶函数加偶函数所得函数是偶函数。</p><p>（3）偶函数加奇函数所得函数为非奇非偶函数。</p><h2 id="_2、奇偶函数的减法规则" tabindex="-1">2、奇偶函数的减法规则 <a class="header-anchor" href="#_2、奇偶函数的减法规则" aria-label="Permalink to &quot;2、奇偶函数的减法规则&quot;">​</a></h2><p>（1）奇函数减去奇函数所得为奇函数。</p><p>（2）偶函数减去偶函数所得为偶函数。</p><p>（3）奇函数减去偶函数所得为非奇非偶函数。</p><h2 id="_3、奇偶函数的乘法规则" tabindex="-1">3、奇偶函数的乘法规则 <a class="header-anchor" href="#_3、奇偶函数的乘法规则" aria-label="Permalink to &quot;3、奇偶函数的乘法规则&quot;">​</a></h2><p>（1）奇函数乘以奇函数所得函数为偶函数。</p><p>（2）奇函数乘以偶函数所得函数为奇函数。</p><p>（3）偶函数乘以偶函数所得为偶函数。</p><h2 id="_4、奇偶函数的除法规则" tabindex="-1">4、奇偶函数的除法规则 <a class="header-anchor" href="#_4、奇偶函数的除法规则" aria-label="Permalink to &quot;4、奇偶函数的除法规则&quot;">​</a></h2><p>（1）奇函数除以奇函数所得函数为偶函数。</p><p>（2）奇函数除以偶函数所得函数为奇函数。</p><p>（3）偶函数除以偶函数所得为偶函数。</p>',22)]))}const f=e(p,[["render",o]]);export{c as __pageData,f as default};
