import{_ as t,c as r,o,aK as i}from"./chunks/framework.gpVTZwhR.js";const g=JSON.parse('{"title":"repute，repetition，representation，reservation，reputation 形近词","description":"","frontmatter":{},"headers":[],"relativePath":"gaozhong/英语/总结文章/单词语法辨析/repute，repetition，representation，reservation，reputation 形近词.md","filePath":"gaozhong/英语/总结文章/单词语法辨析/repute，repetition，representation，reservation，reputation 形近词.md","lastUpdated":1738597217000}'),n={name:"gaozhong/英语/总结文章/单词语法辨析/repute，repetition，representation，reservation，reputation 形近词.md"};function a(p,e,s,u,l,_){return o(),r("div",null,e[0]||(e[0]=[i('<h1 id="repute-repetition-representation-reservation-reputation-形近词" tabindex="-1">repute，repetition，representation，reservation，reputation 形近词 <a class="header-anchor" href="#repute-repetition-representation-reservation-reputation-形近词" aria-label="Permalink to &quot;repute，repetition，representation，reservation，reputation 形近词&quot;">​</a></h1><ul><li><p><strong>repute</strong>：与“reputation”意思相近，都有“名声，声誉”的意思，但“repute”更常用于一些固定搭配或较为正式、书面的表达中，如“of good repute（名声好的）”“have a reputation for（因……而闻名）”。</p></li><li><p><strong>repetition</strong>：名词，意为“重复；反复”。它与“reputation”在拼写和发音上有相似之处，但意思完全不同。例如：“The repetition of the experiment gave consistent results.（实验的重复得出了一致的结果。）”</p></li><li><p><strong>representation</strong>：名词，有“代表；表现；描绘”等意思。它与“reputation”词形相似，但含义不同。如：“The painting is a representation of a rural scene.（这幅画描绘的是一幅乡村景象。）”</p></li><li><p><strong>reservation</strong>：名词，常见的意思有“预订；保留；保护区”等。与“reputation”在词形上有一定相似性，但意义不同。例如：“I made a reservation at the restaurant for 7 o&#39;clock.（我在餐厅预订了 7 点的座位。）”</p></li><li><p><strong>reputation：</strong></p><ul><li>词性：名词，意为“名声；声誉” 。</li><li>短语：have a good/bad reputation for... 因…… 而有好 / 坏名声。</li><li>例句：This restaurant has a good reputation for its delicious food. 这家餐厅因美食而享有好名声。</li></ul></li></ul>',2)]))}const c=t(n,[["render",a]]);export{g as __pageData,c as default};
