import{_ as l,c as e,o as i,aK as t}from"./chunks/framework.gpVTZwhR.js";const h=JSON.parse('{"title":"登高","description":"","frontmatter":{},"headers":[{"level":2,"title":"文","slug":"文","link":"#文","children":[]}],"relativePath":"gaozhong/语文/古诗文/登高.md","filePath":"gaozhong/语文/古诗文/登高.md","lastUpdated":1730896011000}'),o={name:"gaozhong/语文/古诗文/登高.md"};function r(s,a,n,d,_,p){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="登高" tabindex="-1">登高 <a class="header-anchor" href="#登高" aria-label="Permalink to &quot;登高&quot;">​</a></h1><h2 id="文" tabindex="-1">文 <a class="header-anchor" href="#文" aria-label="Permalink to &quot;文&quot;">​</a></h2><p>——杜甫</p><p>风急天高猿啸哀，渚清沙白鸟飞回。</p><p>无边落木萧萧下，不尽长江滚滚来。</p><p>万里悲秋常作客，百年多病独登台。</p><p>艰难苦恨繁霜鬓，潦倒新停浊酒杯。</p><details class="details custom-block"><summary>注意</summary><ol><li>猿，人猿。</li><li>区别哀字和衰字，衰字多一横，表示皱纹</li><li>啸字。呼啸，口字旁；和下一句的萧字形成对照，下一句的萧是草字头。啸字上面的缺左边一竖的口的中间的一横要穿出来</li><li>萧萧下。萧萧，草木摇动的声音，所以是草字头。</li><li>繁霜鬓。鬓字，宾字底。上部分的右边有三撇。</li><li>潦倒。倒字是倒立的倒。</li><li>新停，停是停止的停。</li><li>酒杯，酒字，三点水加酉字（酉阳的酉），不是西方的西</li><li>无边……，不尽……。不是无边……，无尽……，这样显得重复了。</li><li>万里悲秋常作客，百年多病独登台。是作客，而不是做客。“作客”偏书面，意味寄居在他乡，比如常写的“作客他乡”，不能写作“做客他乡”；“做客”口语常用，表示访问别人，自己当客人。这里是诗，所以用书面语“作客”。</li></ol></details>',8)]))}const m=l(o,[["render",r]]);export{h as __pageData,m as default};
