import{_ as l,C as o,c as p,o as i,G as t,aK as n}from"./chunks/framework.gpVTZwhR.js";const S=JSON.parse('{"title":"","description":"","frontmatter":{"label":["文学类文本阅读"],"level":null},"headers":[],"relativePath":"gaozhong/语文/题/文学类文本阅读/01.md","filePath":"gaozhong/语文/题/文学类文本阅读/01.md","lastUpdated":1739462050000}'),c={name:"gaozhong/语文/题/文学类文本阅读/01.md"};function _(r,s,m,u,d,g){const e=o("ShowLabels"),a=o("RC");return i(),p("div",null,[t(e),t(a,{count:"1",msg:[""]}),s[0]||(s[0]=n('<img src="https://my-course-public.oss-cn-shanghai.aliyuncs.com/xuexi/Snipaste_2024-12-31_05-57-51.png"><img src="https://my-course-public.oss-cn-shanghai.aliyuncs.com/xuexi/Snipaste_2024-12-31_05-58-13.png"><img src="https://my-course-public.oss-cn-shanghai.aliyuncs.com/xuexi/Snipaste_2024-12-31_05-58-35.png"><details class="details custom-block"><summary>solution</summary><p>（6）</p><p>A 错。是主持人告诉他有心理问题。</p><p>B 错。惯常做法是当面调解。但刘主任和曾明出去一起走走，还牵手，不符合惯常做法。</p><p>C 错。构成了小说的基调与主题错误。关于这个问题作者没有深入阐发，只是留下疑问引发读者深思。</p><p>C 对。</p><p>（7）D。</p><p>本体考察对文本艺术特色的分析鉴赏能力。D 项，语言风格质朴平实不能推导出作者对盲人世界的认识，属于逻辑推理错误。</p><p>（8）</p><p>直接抄原文中刘主任的一些句子和话。</p><p>（9）</p></details><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><strong>第一人称叙事</strong>的好处：</p><ol><li>使读者更易体会到主人公的心理状态，感同身受</li><li>使故事更具真实感，拉进与读者的距离</li><li>从我的角度观察，让刘主任的行为和言语显得有些奇怪，引发读者的好奇与想象</li><li>使得刘主任是盲人这一重要事实直到最后才揭示，有出人意料的效果。</li><li>“我”是故事的见证者与参与者，使情节更加集中紧凑</li><li>通过“我”的视角展现出曾明的人物形象，有利于塑造人物形象</li><li>“我”的所见所闻所感，有助于揭示小说主题</li></ol></div>',5))])}const b=l(c,[["render",_]]);export{S as __pageData,b as default};
